import { useState } from 'react';
import './App.css';
import About from './components/About';
import Navbar from './components/Navbar';
import TextForm from './components/TextForm';
import Alert from './components/Alert';

function App() {
  const[mode,setMode]=useState('light');
  const [alert, setAlert] = useState(null);
  const [toggle,setToggle]=useState('off')
  const isDisabled=()=>{
    if (toggle==='on')
    {
      return 'true';
    }
    return 'false';
  }
  const greenToggleMode=()=>{
    if(mode==='light')
    {
      setMode('green');
      setToggle('on');
      document.body.style.backgroundColor='#1c6a53';
      showAlert("Dark mode is enabled","success");
    }
    else
    {
    setMode('light');
    setToggle('off')
    document.body.style.backgroundColor='white';
    showAlert("Light mode is enabled","success");
    }
  }  

  const toggleMode=()=>{
    if(mode==='light')
    {
      setMode('dark');
      setToggle('on');
      document.body.style.backgroundColor='#042743';
      showAlert("Dark mode is enabled","success");
    }
    else
    {
    setMode('light');
    setToggle('off');
    document.body.style.backgroundColor='white';
    showAlert("Light mode is enabled","success");
    }
  }



  const showAlert=(message,type)=>{
      setAlert({msg:message,
      type:type})
      setTimeout(() => {
        setAlert(null)
      }, 2000);
  }
  return (

    <><Navbar title="TextUtils" aboutText="About Us"  mode={mode} setT toggleMode={toggleMode} greenToggleMode={greenToggleMode} isDisabled={isDisabled}/>
    <Alert alert={alert}/>
    <div className='container my-3' >
      <h2 style={{color:mode==='light'?'black':'white'}}>Enter your text to analyze</h2>
    <TextForm mode={mode} showAlert={showAlert}/>
    <About></About>
    </div>
    </>
  );
}

export default App;
