import React,{useState} from 'react'

export default function TextForm(props) {
    const handleUpClick = ()=>{
        let newText=text.toUpperCase();
        setText(newText)
    }
    const handleLcClick = ()=>{
        let newText=text.toLowerCase();
        setText(newText)
    }
    const handleClearClick = ()=>{
      let newText='';
      setText(newText)
  }
    const handleOnChange = (event)=>{
        setText(event.target.value);
    }
    const[text,setText]=useState('');
  return (
    <div className="form-group">
    <label htmlFor="exampleFormControlTextarea1">{props.heading}</label>
    <br></br>
    <br></br>
    <textarea className="form-control" id="exampleFormControlTextarea1" rows="8" value={text} onChange={handleOnChange} placeholder="Enter your text here"></textarea>
    <br></br>
    <div className='container'>
    <button className="btn btn-primary mx-2" onClick={handleUpClick}>Convert to UpperCase</button> 
    <button className="btn btn-primary mx-2" onClick={handleLcClick}>Convert to LowerCase</button>
    <button className="btn btn-primary mx-2" onClick={handleClearClick}>Clear Text</button>
    </div>
    <div className='container my-4'>
      <h2>Your Text Summary</h2>
      <p>{text.split(" ").length-1} words and {text.length} characters</p>
      <p>{0.008*text.split(" ").length} minutes to read</p>
      <h2>Preview</h2>
      <p>{text}</p>
    </div>
  </div>
  )
}
